//app.js
import * as AuthSession from "expo-auth-session";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import { Alert, Button, Platform, StyleSheet, Text, View, Image } from "react-native";
import Dictionary from './Dictionary';  // Import the Dictionary component
import QuizComponent from './QuizComponent';  // Import the Dictionary component
import Navbar from './Navbar';
import Saved from './Saved';
import Content from './Content';  // Import the Content component



export default function App() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = async () => {
        try {
            let response = await fetch('YOUR_PHP_LOGIN_ENDPOINT', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    username: username,
                    password: password,
                }),
            });

            let json = await response.json();
            if (json.success) {
                setIsLoggedIn(true);
            } else {
                Alert.alert('Login Failed', json.message);
            }
        } catch (error) {
            console.error('Login error:', error);
        }
    };

    return (
        <View style={styles.container}>
            {isLoggedIn ? (
                <>
                    {/* Your logged in components here */}
                    <Button title="Log out" onPress={() => setIsLoggedIn(false)} />
                </>
            ) : (
                <>
                    <TextInput
                        placeholder="Username"
                        value={username}
                        onChangeText={setUsername}
                    />
                    <TextInput
                        placeholder="Password"
                        value={password}
                        onChangeText={setPassword}
                        secureTextEntry={true}
                    />
                    <Button title="Login" onPress={handleLogin} />
                </>
            )}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
        justifyContent: 'flex-start',
    },
    Button:{ width:100},
    title: {
        fontSize: 20,
        textAlign: "center",
        marginTop: 40
    },
    image: {
        width: 300, // Or adjust based on your requirements
        height: 200, // Or adjust based on your requirements
        resizeMode: 'contain', // Ensures image is scaled correctly
        alignSelf: 'center', // Centers the image horizontally
        marginTop: 50 // Spacing from the top
    }
});
