//app.js
import * as AuthSession from "expo-auth-session";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import { Alert, Button, StyleSheet, Text, View, Image, TextInput } from 'react-native';
import Dictionary from './Dictionary';  // Import the Dictionary component
import QuizComponent from './QuizComponent';  // Import the Dictionary component
import Navbar from './Navbar';
import Saved from './Saved';
import Content from './Content';  // Import the Content component



export default function App() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
const [registerUsername, setRegisterUsername] = useState('');
const [registerPassword, setRegisterPassword] = useState('');
const [registerEmail, setRegisterEmail] = useState('');
const handleRegister = async () => {
    try {
        let response = await fetch('https://skeba.info/applogin/register.php', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                username: registerUsername,
                password: registerPassword,
                email: registerEmail,
            }),
        });

        let json = await response.json();
        if (json.success) {
            Alert.alert('Registration Successful', 'You can now log in.');
            // Clear the registration fields
            setRegisterUsername('');
            setRegisterPassword('');
            setRegisterEmail('');
        } else {
            Alert.alert('Registration Failed', json.message);
            // Optionally, clear the registration fields even on failure if desired
            // setRegisterUsername('');
            // setRegisterPassword('');
            // setRegisterEmail('');
        }
    } catch (error) {
        console.error('Registration error:', error);
    }
};

    const handleLogin = async () => {
        try {
            let response = await fetch('https://skeba.info/applogin/login.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    username: username,
                    password: password,
                }),
            });

            let json = await response.json();
            if (json.success) {
                setIsLoggedIn(true);
            } else {
                Alert.alert('Login Failed', json.message);
            }
        } catch (error) {
            console.error('Login error:', error);
        }
    };

  return (
        <View style={styles.container}>
            {isLoggedIn ? (
                <View style={styles.loggedInContainer}>
                    {/* Your logged in components here */}
                    <Button title="Log out" onPress={() => setIsLoggedIn(false)} />
                </View>
            ) : (
                <View style={styles.formContainer}>
                            <Image source={require('./data/spanishquiz.png')} style={styles.image} />

                    <Text style={styles.header}>Login</Text>
                    <TextInput
                        style={styles.input}
                        placeholder="Username"
                        value={username}
                        onChangeText={setUsername}
                    />
                    <TextInput
                        style={styles.input}
                        placeholder="Password"
                        value={password}
                        onChangeText={setPassword}
                        secureTextEntry={true}
                    />
                    <Button title="Login" onPress={handleLogin} />

                    <Text style={[styles.header, styles.marginTop]}>Register</Text>
                    <TextInput
                        style={styles.input}
                        placeholder="Register Username"
                        value={registerUsername}
                        onChangeText={setRegisterUsername}
                    />
                    <TextInput
                        style={styles.input}
                        placeholder="Register Email"
                        value={registerEmail}
                        onChangeText={setRegisterEmail}
                    />
                    <TextInput
                        style={styles.input}
                        placeholder="Register Password"
                        value={registerPassword}
                        onChangeText={setRegisterPassword}
                        secureTextEntry={true}
                    />
                    <Button title="Register" onPress={handleRegister} />
                </View>
            )}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#f4f4f4",
        padding: 20,
    },
       image: {
        width: 300,
        height: 200,
        resizeMode: 'contain',
        alignSelf: 'center',
        marginBottom: 40,
    },
    loggedInContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
    },
    formContainer: {
        flex: 1,
        justifyContent: 'center',
    },
    header: {
        fontSize: 24,
        fontWeight: 'bold',
        textAlign: 'center',
        marginBottom: 20,
    },
    input: {
        padding: 10,
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
        marginBottom: 15,
        backgroundColor: '#fff',
    },
    marginTop: {
        marginTop: 40,
    }
});
