import './App.css';
import React, { useState, useEffect } from 'react';
import QuizComponent from './QuizComponent';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [difficulty, setDifficulty] = useState('beginner');
  const [language, setLanguage] = useState('english');
  function handleAuthAction() {
    if (isLoggedIn) {
      // Log out the user by removing the access token from the URL and updating the isLoggedIn state
      window.location.hash = '';
      setIsLoggedIn(false);
    } else {
      // Google's OAuth 2.0 endpoint for requesting an access token
      var oauth2Endpoint = 'https://accounts.google.com/o/oauth2/v2/auth';

      // Create <form> element to submit parameters to OAuth 2.0 endpoint.
      var form = document.createElement('form');
      form.setAttribute('method', 'GET'); // Send as a GET request.
      form.setAttribute('action', oauth2Endpoint);

      // Parameters to pass to OAuth 2.0 endpoint.
      var params = {
        'client_id': '818715484116-lk0u6hiqoi4s4rgt559pq7i3v7p06eep.apps.googleusercontent.com',
        'redirect_uri': 'http://localhost:3000/',
        'response_type': 'token',
        'scope': 'https://www.googleapis.com/auth/drive.metadata.readonly',
        'include_granted_scopes': 'true',
        'state': 'pass-through value'
      };

      // Add form parameters as hidden input values.
      for (var p in params) {
        var input = document.createElement('input');
        input.setAttribute('type', 'hidden');
        input.setAttribute('name', p);
        input.setAttribute('value', params[p]);
        form.appendChild(input);
      }

      // Add form to page and submit it to open the OAuth 2.0 endpoint.
      document.body.appendChild(form);
      form.submit();
    }
  }

  useEffect(() => {
    // Check if the user is logged in by checking the URL for an access token
    if (window.location.hash.includes('access_token')) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <div>
      <nav className="navbar navbar-light bg-light">
        <a className="navbar-brand" href="#">Quiz App</a>
        {isLoggedIn && (
          <>
            <div className="dropdown">
              <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                Difficulty
              </button>
              <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                <button className={difficulty === 'beginner' ? 'dropdown-item selected-difficulty' : 'dropdown-item'} onClick={() => setDifficulty('beginner')}>Beginner</button>
                <button className={difficulty === 'intermediate' ? 'dropdown-item selected-difficulty' : 'dropdown-item'} onClick={() => setDifficulty('intermediate')}>Intermediate</button>
                <button className={difficulty === 'advanced' ? 'dropdown-item selected-difficulty' : 'dropdown-item'} onClick={() => setDifficulty('advanced')}>Advanced</button>
              </div>
            </div>
            <div className="dropdown">
              <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                Language
              </button>
              <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                <button className={language === 'english' ? 'dropdown-item selected-language' : 'dropdown-item'} onClick={() => setLanguage('english')}>English</button>
                <button className={language === 'spanish' ? 'dropdown-item selected-language' : 'dropdown-item'} onClick={() => setLanguage('spanish')}>Spanish</button>
                <button className={language === 'german' ? 'dropdown-item selected-language' : 'dropdown-item'} onClick={() => setLanguage('german')}>German</button>
                <button className={language === 'french' ? 'dropdown-item selected-language' : 'dropdown-item'} onClick={() => setLanguage('french')}>French</button>
              </div>
            </div>
          </>
        )}
                <button className="btn btn-outline-success my-2 my-sm-0" type="button" onClick={handleAuthAction}>{isLoggedIn ? 'Logout' : 'Login with Google'}</button>

      </nav>
      <div className="App">
      {isLoggedIn ? (
         <QuizComponent difficulty={difficulty} language={language} />
      ) : (
        <div style={{ width: '500px', height: '700px', margin: 'auto', position: 'absolute', top: 0, left: 0, bottom: 0, right: 0, backgroundColor: 'lightgray', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ textAlign: 'center' }}>
            <h1>Welcome to Quiz App</h1>
          </div>
        </div>
      )}
      </div>
    </div>
  );
}

export default App;
