import './App.css';
import React, { useState, useEffect } from 'react';
import QuizComponent from './QuizComponent';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [difficulty, setDifficulty] = useState('beginner');
  const [language, setLanguage] = useState('english');
  const [quizName, setQuizName] = useState('Beginner');
  const [languageName, setLanguageName] = useState('Spanish');
  const [quizType, setQuizType] = useState('Subjunctive');


  function handleAuthAction() {
    if (isLoggedIn) {
      // Log out the user by removing the access token from the URL and updating the isLoggedIn state
      window.location.hash = '';
      setIsLoggedIn(false);
    } else {
      // Google's OAuth 2.0 endpoint for requesting an access token
      var oauth2Endpoint = 'https://accounts.google.com/o/oauth2/v2/auth';

      // Create <form> element to submit parameters to OAuth 2.0 endpoint.
      var form = document.createElement('form');
      form.setAttribute('method', 'GET'); // Send as a GET request.
      form.setAttribute('action', oauth2Endpoint);

      // Parameters to pass to OAuth 2.0 endpoint.
      var params = {
        'client_id': '818715484116-lk0u6hiqoi4s4rgt559pq7i3v7p06eep.apps.googleusercontent.com',
        'redirect_uri': 'http://localhost:3000/',
        'response_type': 'token',
        'scope': 'https://www.googleapis.com/auth/drive.metadata.readonly profile',
        'include_granted_scopes': 'true',
        'state': 'pass-through value'

      };

      // Add form parameters as hidden input values.
      for (var p in params) {
        var input = document.createElement('input');
        input.setAttribute('type', 'hidden');
        input.setAttribute('name', p);
        input.setAttribute('value', params[p]);
        form.appendChild(input);
      }

      // Add form to page and submit it to open the OAuth 2.0 endpoint.
      document.body.appendChild(form);
      form.submit();
    }
  }

  useEffect(() => {
    // Check if the user is logged in by checking the URL for an access token
    if (window.location.hash.includes('access_token')) {
      setIsLoggedIn(true);

      const hash = window.location.hash
      .substring(1)
      .split('&')
      .reduce((init, item) => {
        let parts = item.split('=');
        init[parts[0]] = decodeURIComponent(parts[1]);
        return init;
      }, {});
    const idToken = JSON.parse(atob(hash.id_token.split('.')[1]));
    setProfile({
      name: idToken.name,
      email: idToken.email,
      // ... other profile fields
    });
  }
}, []);
  function handleDifficultyChange(difficulty, name) {
    setDifficulty(difficulty);
    setQuizName(name);
  }

  function handleLanguageChange(language, name) {
    setLanguage(language);
    setLanguageName(name);
  }

  function handleQuizTypeChange(type) {
    setQuizType(type);
  }
  return (
    <div>
      <nav className="navbar navbar-light bg-light">
        <a className="navbar-brand" href="#">Quiz App</a>
        {isLoggedIn && (
        <>
        <div className="dropdown">
          <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            {quizName}
          </button>
          <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
            <button className="dropdown-item" onClick={() => handleDifficultyChange('beginner', 'Beginner')}>Beginner</button>
            <button className="dropdown-item" onClick={() => handleDifficultyChange('intermediate', 'Intermediate')}>Intermediate</button>
            <button className="dropdown-item" onClick={() => handleDifficultyChange('advanced', 'Advanced')}>Advanced</button>
          </div>
        </div>
        <div className="dropdown">
          <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            {languageName}
          </button>
          <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
            <button className="dropdown-item" onClick={() => handleLanguageChange('english', 'English')}>English</button>
            <button className="dropdown-item" onClick={() => handleLanguageChange('spanish', 'Spanish')}>Spanish</button>
            <button className="dropdown-item" onClick={() => handleLanguageChange('german', 'German')}>German</button>
            <button className="dropdown-item" onClick={() => handleLanguageChange('french', 'French')}>French</button>
          </div>
        </div>
        <div className="dropdown">
              <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                {quizType}
              </button>
              <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                <button className="dropdown-item" onClick={() => handleQuizTypeChange('Subjunctive')}>Subjunctive</button>
                <button className="dropdown-item" onClick={() => handleQuizTypeChange('Conjugation')}>Conjugation</button>
                <button className="dropdown-item" onClick={() => handleQuizTypeChange('Imperative')}>Imperative</button>
              </div>
            </div>
      </>
        )}
                <button className="btn btn-outline-success my-2 my-sm-0" type="button" onClick={handleAuthAction}>{isLoggedIn ? 'Logout' : 'Login with Google'}</button>

      </nav>
      <div className="App">
      {isLoggedIn ? (
        <QuizComponent difficulty={difficulty} language={language} quizName={quizName} languageName={languageName} quizType={quizType} />
        ) : (
        <div style={{ width: '500px', margin: 'auto', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ textAlign: 'center' }}>
            <span>Unlock a world of language mastery with our intuitive quiz app! Choose from Spanish, French, English, or Italian and hone your skills in subjunctive usage, basic conjugation, and mastering the imperative - transforming your language learning journey into an engaging adventure.</span>
            <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="Logo" />

          </div>
        </div>
      )}
      </div>
    </div>
  );
}

export default App;
