import React from 'react';

function ProfilePage({ profile, defaultSettings, setDefaultSettings }) {
    const [language, setLanguage] = useState(defaultSettings.language);
    const [difficulty, setDifficulty] = useState(defaultSettings.difficulty);

    const handleSave = () => {
        setDefaultSettings({ language, difficulty });
      };
  return (
    <div>
      <h1>Welcome, {profile.name}</h1>
      <h2>Set Your Default Preferences</h2>
      <label>
        Language: 
        <select onChange={(e) => setDefaultSettings(prevState => ({...prevState, language: e.target.value}))}>
          <option value="english">English</option>
          <option value="spanish">Spanish</option>
          {/* Add other options here */}
        </select>
      </label>
      <label>
        Difficulty: 
        <select onChange={(e) => setDefaultSettings(prevState => ({...prevState, difficulty: e.target.value}))}>
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          {/* Add other options here */}
        </select>
      </label>
    </div>
  );
}

export default ProfilePage;
